CREATE TABLE `page_log` (`id` int(11) NOT NULL AUTO_INCREMENT,`page` varchar(255) NOT NULL,  `ajax` varchar(255) NOT NULL,  `success` int(11) NOT NULL,  `user_obj` varchar(255) NOT NULL,  `time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  `ip` varchar(255) NOT NULL,  `obj` mediumtext NOT NULL,  PRIMARY KEY (`id`),  KEY `user_obj` (`user_obj`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
